import React from 'react';
import Header from '../Children/Header';
import { useState } from "react";
import MoviePromo from '../Children/MoviePromo';
import ShowPromo from '../Children/ShowPromo';
import '../../assets/css/TvShows.css';



const Tvshows = () => {
    const [movies, setMovies] = useState([
        {id : 1, title : "Spirited Away", genre : "Anime", director : "Hayao Miyazaki", year : 2001, image : 'https://m.media-amazon.com/images/M/MV5BMjlmZmI5MDctNDE2YS00YWE0LWE5ZWItZDBhYWQ0NTcxNWRhXkEyXkFqcGdeQXVyMTMxODk2OTU@._V1_.jpg'},
        {id : 2, title : "Castle in the Sky", genre : "Anime", director : "Hayao Miyazaki", year : 2003, image : 'https://upload.wikimedia.org/wikipedia/en/f/f5/Castle_in_the_Sky_%281986%29.png'},
        {id : 3, title : "The Irishman", genre : "Gangster", director : "Martin Scorsese", year : 2019},
        {id : 4, title : "God Father", genre : "Gangster", director : "Francis Ford Coppola", year : 1972},
        {id : 5, title : "Inception", genre : "Drame", director : "Christopher Nolan", year : 2010},
        {id : 6, title : "Her", genre : "Romance", director : "Spike Jonze", year : 2014},
        {id : 7, title : "It", genre : "Horror", director : "Andrés Muschietti", year : 20017},
        {id : 8, title : "Conjuring 3 : The Devil Made Me Do It", genre : "Horror", director : "Michael Chaves", year : 2021},
        {id : 9, title : "The Green Miles", genre : "Drama", director : "Frank Darabont", year : 2000},
        {id : 10, title : "Full Metal Jacket", genre : "War", director : "Stanley Kubrick", year : 1987},
    ]);
    return (
        <div>
            <Header/>
            <main>
            {movies.map((movie) => {
            return (
              <article className='articleContainer'>
                <div className='articleMovie'>
                <h2>{movie.title}</h2>
                <p>{movie.genre}</p>
                <p>{movie.director}</p>
                <p className='year'>{movie.year}</p>
                <div className='articleImage'>
                <img src={movie.image} alt="" />
                </div>
                </div>
              </article>
            );
          })}
            </main>
        </div>
    );
};

export default Tvshows;
