import React, { useRef} from "react";
import { NavLink } from "react-router-dom";
import "../../assets/css/Header.css";

const Header = () => {

  const showModal = () =>{
    const modalRef = document.getElementsByClassName('.modalContainer')
    modalRef.classList.remove('.displayNone');
  }
  showModal()
  return (
    <header>
      <nav id="navigation">
        <div id="logoNav"></div>
        <div id="websiteName">
          <h1>
            <NavLink to={"/"}>Viddev</NavLink>
          </h1>
        </div>
        <div id="linksNav">
          <ul>
            <li>
              <NavLink to={"/tvshows"}><a>TV Shows</a></NavLink>
            </li>
            <li>
              <NavLink to={"/movies"}><a>Movie</a></NavLink>
            </li>
            <li>
              <NavLink to={"/contact"}><a>Contact Us</a></NavLink>
            </li>
            <li>
              <a onClick={showModal}>Login</a>
            </li>
            <li>
              <NavLink to={"/registrer"}>Registrer</NavLink>
            </li>
          </ul>
        </div>
      </nav>
      <div className="modalContainer displayNone">
      </div>
    </header>
  );
};

export default Header;
